mapper.insert:
INSERT INTO CATEGORY (
	category_id,
	category_name
)
VALUES (
	SEQ_CATEGORY.NEXTVAL,
	#{categoryName}
)

mapper.update:
UPDATE CATEGORY
SET category_name = #{categoryName}
WHERE category_id = #{categoryId}

mapper.insert:
INSERT INTO HASHTAG (
	hashtag_id,
	hashtag_name
)
VALUES (
	SEQ_HASHTAG.NEXTVAL,
	#{hashtagName}
)

mapper.update:
UPDATE HASHTAG
SET hashtag_name = #{hashtagName}
WHERE hashtag_id = #{hashtagId}

mapper.select:
SELECT 
	event_id,
	hashtag_id
FROM EVENT_HASHTAG
WHERE event_id = #{eventId}
	AND hashtag_id = #{hashtagId}

mapper.insert:
INSERT INTO EVENT_HASHTAG (
	event_id,
	hashtag_id
)
VALUES (
	#{eventId},
	#{hashtagId}
)

mapper.delete:
DELETE
FROM EVENT_HASHTAG
WHERE event_id = #{eventId}
	AND hashtag_id = #{hashtagId}

mapper.insert:
INSERT INTO BANNER (
	event_id,
	order_priority
)
VALUES (
	#{eventId},
	#{orderPriority}
)

mapper.update:
UPDATE BANNER
SET order_priority = #{orderPriority}
WHERE event_id = #{eventId}
